pragma solidity ^0.4.18;

contract ChannelManager {
    struct Channel {
        address member1;
        address member2;
        uint startDate;
        uint timeout;
        mapping (bytes32 => address) signatures;
        mapping (address => uint) balances;
    }

    Channel public openedChannel;

    function openChannel(uint timeout) payable public {
        openedChannel = Channel(
            msg.sender,
            address(0), // no member2 yet,
            now,
            timeout
        );
    }

    function joinChannel() public payable {
        openedChannel.member2 = msg.sender;
    }

    function closeChannel(bytes32 h, uint8 v, bytes32 r, bytes32 s, uint value1, uint value2) public {
		address signer;
		bytes32 proof;

		// get signer from signature
		signer = ecrecover(h, v, r, s);

		// signature is invalid, throw
		require(signer == openedChannel.member1 || signer == openedChannel.member2);

		proof = keccak256(this, value1, value2);

		// signature is valid but doesn't match the data provided
		require(proof == h);

		if (openedChannel.signatures[proof] == 0)
			openedChannel.signatures[proof] = signer;
		else if (openedChannel.signatures[proof] != signer) {
			// channel completed, both signatures provided
            openedChannel.member1.transfer(value1);
            openedChannel.member2.transfer(value1);
			delete openedChannel;
		}

	}

}
